mapper.select:
SELECT
a.id,a.no,a.tracking_no,a.cus_code,a.cus_name,a.currency_code,a.currency_name,a.amount,
a.warehouse_code,a.warehouse_name,a.pay_method,a.business_category,a.product_code,a.product_category,
a.charge_category,a.charge_type,a.create_by_name,a.create_time,a.update_by_name,a.update_time,
a.create_by,a.update_by,a.remark,a.order_time,a.payment_time,a.ref_no,a.shipment_service,a.weight,
a.calc_weight,a.specifications

FROM
`fss_account_serial_bill` a


1=1




    and a.no IN

        #{item}



  and a.cus_code IN

            #{item}



        and a.product_code IN

            #{item}




        and a.charge_type =#{chargeType}


        and a.warehouse_code =#{warehouseCode}



        and a.currency_code =#{currencyCode}


    and a.business_category =#{businessCategory}


    and a.product_category =#{productCategory}


    and a.charge_category =#{chargeCategory}


    and date_format(a.create_time,'%y%m%d') >= date_format(#{createTimeStart},'%y%m%d')



    and date_format(a.create_time,'%y%m%d') <= date_format(#{createTimeEnd},'%y%m%d')


    and FIND_IN_SET(a.id,#{ids})


ORDER BY a.payment_time DESC

select FASB.business_category,FASB.`no`,FASB.currency_code, DOB.trace_id,DOB.ref_no,DOB.warehouse_code, DOB.destination_delivery,DOB.weight,DOB.forecast_weight, DOB.calc_weight,DOB.create_time, DOB.specifications, DOD.product_name,DOD.sku, sum(FASB.amount) as total_amount, sum(case when FASB.charge_category = '物流基础费' then abs(FASB.amount) else 0 end ) as freight_fee, sum(case when FASB.charge_category = '操作费' or FASB.charge_category = '特殊操作费' or FASB.charge_category='业务操作' then abs(FASB.amount) else 0 end) as ex_warehourse_fee, sum(case when FASB.charge_category = '物料费' then abs(FASB.amount) else 0 end) as package_fee, sum(case when FASB.charge_category = '处理费' then abs(FASB.amount) else 0 end) as hander_fee, sum(case when FASB.charge_category = '燃油附加费' then abs(FASB.amount) else 0 end) as fuel_fee, sum(case when FASB.charge_category = '偏远地区费' then abs(FASB.amount) else 0 end) as remote_fee, sum(case when FASB.charge_category = '偏远地区费' or FASB.charge_category = '住宅派送费' or FASB.charge_category = '增值税' or FASB.charge_category = '挂号费' or FASB.charge_category = '超大附加费' or FASB.charge_category = '超重处理费' or FASB.charge_category = '退费'
then abs(FASB.amount) else 0 end) as other_fee from fss_account_serial_bill as FASB inner join del_outbound as DOB on FASB.`no` = DOB.order_no left join del_outbound_detail as DOD on DOB.order_no = DOD.order_no
and DOB.order_type in ('PackageTransfer') and DOB.del_flag = 0
and FASB.cus_code = #{cusCode}
and FASB.payment_time >= #{billStartTime}
and FASB.payment_time <= #{billEndTime}
group by FASB.business_category,FASB.no,FASB.currency_code
